    // })
    // return (
    //     <div>
    //         {roobotsArray}
    //     </div>
    // );
    return (
        <div>
            {
                robots.map((user, i) => {
                    return <Card key={i} username={robots[i].username} name={robots[i].name} email={robots[i].email} />
                })
            }
        </div>
    );
}

